let id = Inputs.taskObjectId;  
let taskk = Inputs.task;       
let obj = Inputs.existingObject;
// CHEC
if (obj !== undefined){
    Object.assign(obj, { [id]: { "task" : taskk } });
} else {
    obj = {
        [id] : {
            "task" : taskk
        }
    };
}
Outputs.newObject = obj;
Outputs.newObjectCreated();

