let newId = Inputs.taskId;
let newTask = Inputs.updatedTask;
let tasks = Inputs.tasks;
Object.keys(tasks).forEach(key => {
    if (tasks[key].id === newId) {
        tasks[key].task = newTask;
    }
});

let obj = Inputs.tasks
let taskid = Inputs.task_id
let filteredTasks = Object.fromEntries(
    Object.entries(obj).filter(([key, value]) => value.id !== taskid)
);
Outputs.deletedTasks = filteredTasks
Outputs.send()
